<?phpclass Lerntutoren extends AbstractPage {	private $isAdmin = false;		public function __construct() {		parent::__construct(array("Laufzettel"));		$this->checkLogin();	}	public function execute() {	    $this->isAdmin = DB::getSession()->isMember('Webportal_Lerntutoren_Admin');	    if(DB::getSession()->isAdmin()) $this->isAdmin = true;		$mode = $_REQUEST['mode'];		switch($mode) {			default:				$this->showIndex();			break;            case 'contact':                $this->contact();                break;		}	}	private function showIndex() {		eval("echo(\"" . DB::getTPL()->get("lerntutoren/schuelereltern/index") . "\");");	}    private function contact() {        eval("echo(\"" . DB::getTPL()->get("lerntutoren/schuelereltern/contact") . "\");");    }	public static function getNotifyItems() {		return array();	}	public static function hasSettings() {		return true;	}		public static function displayAdministration($selfURL) {	    $html = "";	    eval("\$html = \"" . DB::getTPL()->get("lerntutoren/admin/index") . "\";");	    	    return $html;	}	public static function getSettingsDescription() {		return [		    [		        'name' => "lerntutoren-disclaimer",		        'typ' => 'TEXT',		        'titel' => "Generelle Hinweise für die Lerntutoren",		        'text' => ""		    ]        ];	}	public static function getSiteDisplayName() {		return 'Lerntutoren';	}	/**	 * Liest alle Nutzergruppen aus, die diese Seite verwendet. (Für die Benutzeradministration)	 * @return array(array('groupName' => '', 'beschreibung' => ''))	 */	public static function getUserGroups() {		return array();	}	public static function hasAdmin() {		return true;	}		public static function getAdminMenuIcon() {		return 'fa fa-graduation-cap';	}		public static function getAdminGroup() {		return 'Webportal_Lerntutoren_Admin';	}		public static function getAdminMenuGroup() {		return 'Lerntutoren';	}		public static function getAdminMenuGroupIcon() {		return 'fa fa-graduation-cap';	}}?>